    <div style="width: 60%;">
        <p>Supplier : {{ customer }}</p>
        {% if address %}
            {% if address.unit_number %}
                <p>{{address.unit_number}},</p>
            {% endif %}
            
            <p>{{address.address_line1}},</p>
            
            {% if address.address_line2 %}
                <p>{{address.address_line2}},</p>
            {% endif %}
            
            
            <p>{% if address.pincode %}{{ address.pincode }}{% if address.city %} {% endif %}{% endif %}{{ address.city }},</p>
            
            <p>{{address.state or ''}}</p>
        
